import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../../components/Header';
import { authService, utils } from '../../services/api';
import { apiCall } from '../../services/urlHelper';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Dans Dashboard.jsx, remplace fetchDashboardData par :

// Dans Dashboard.jsx, remplace fetchDashboardData par cette version ultra-debug :

const fetchDashboardData = async () => {
  try {
    const authToken = localStorage.getItem('auth_token');
    
    if (!authToken) {
      navigate('/services');
      return;
    }

    // âœ… Ã‰TAPE 1 : Test de base - Est-ce que le backend rÃ©pond ?
    console.log('ğŸ§ª [TEST 1] Test ping backend simple...');
    
    try {
      const pingResponse = await fetch('http://192.168.0.106:8000/', {
        method: 'GET',
        mode: 'cors'
      });
      console.log('ğŸ“ [TEST 1] Ping backend status:', pingResponse.status);
    } catch (pingError) {
      console.error('âŒ [TEST 1] Ping backend FAILED:', pingError);
    }

    // âœ… Ã‰TAPE 2 : Test API direct simple
    console.log('ğŸ§ª [TEST 2] Test API route simple...');
    
    try {
      const simpleApiResponse = await fetch('http://192.168.0.106:8000/api/test-profile', {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
        },
        mode: 'cors'
      });
      console.log('ğŸ”§ [TEST 2] API simple status:', simpleApiResponse.status);
      const simpleData = await simpleApiResponse.text();
      console.log('ğŸ”§ [TEST 2] API simple response:', simpleData);
    } catch (apiError) {
      console.error('âŒ [TEST 2] API simple FAILED:', apiError);
    }

    // âœ… Ã‰TAPE 3 : Test avec token
    console.log('ğŸ§ª [TEST 3] Test avec authentification...');
    
    const userType = localStorage.getItem('user_type');
    const endpoint = userType === 'retraite' ? 'retraites' : 'actifs';
    const dashboardUrl = `http://192.168.0.106:8000/api/${endpoint}/dashboard`;
    
    console.log('ğŸŒ [TEST 3] URL:', dashboardUrl);
    console.log('ğŸ”‘ [TEST 3] Token:', authToken ? 'PRÃ‰SENT' : 'ABSENT');
    console.log('ğŸ‘¤ [TEST 3] User type:', userType);
    
    const response = await fetch(dashboardUrl, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${authToken}`,
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      mode: 'cors'
    });

    console.log('ğŸ“¡ [TEST 3] Dashboard response status:', response.status);
    console.log('ğŸ“¡ [TEST 3] Response headers:', [...response.headers.entries()]);
    
    if (!response.ok) {
      const errorText = await response.text();
      console.error('âŒ [TEST 3] Dashboard error body:', errorText);
      throw new Error(`HTTP ${response.status}: ${errorText}`);
    }

    const data = await response.json();
    console.log('ğŸ“Š [TEST 3] Dashboard data:', data);

    if (data.success) {
      setDashboardData(data);
      localStorage.setItem('user_type', data.user_type);
      console.log('âœ… [SUCCESS] Dashboard chargÃ© !');
    } else {
      throw new Error(data.message || 'Erreur de chargement');
    }

  } catch (error) {
    console.error('ğŸ’¥ [FINAL ERROR] Erreur dashboard complÃ¨te:', {
      message: error.message,
      name: error.name,
      stack: error.stack,
      cause: error.cause
    });
    
    // âœ… PAS DE REDIRECTION POUR L'INSTANT - on debug
    setError(`Erreur: ${error.message}`);
    
  } finally {
    setLoading(false);
  }
};

  useEffect(() => {
    fetchDashboardData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const handleLogout = async () => {
    try {
      await authService.logout();
    } catch (error) {
      console.error('Erreur dÃ©connexion:', error);
    } finally {
      utils.clearSession();
      navigate('/services');
    }
  };

  // Fonction pour gÃ©rer les clics sur les services
  const handleServiceClick = (serviceId) => {
    const userType = localStorage.getItem('user_type');
    
    // Construire l'URL selon le type d'utilisateur
    const serviceUrls = {
      actif: {
      simulateur_pension: '/actifs/simulateur-pension',
      grappe_familiale: '/actifs/grappe-familiale',
      cotisations: '/actifs/cotisations',
      prise_rdv: '/actifs/rendez-vous',
      attestations: '/actifs/attestations',
      profil: '/actifs/profil',
      reclamations: '/actifs/reclamations',
      },
     retraite: {
      pension: '/retraites/pension',
      grappe_familiale: '/retraites/grappe-familiale', 
      certificats: '/retraites/certificats-vie',
      historique: '/retraites/historique',
      attestations: '/retraites/attestations',
      profil: '/retraites/profil'
      }
    };

    const targetUrl = serviceUrls[userType]?.[serviceId];
    if (targetUrl) {
      navigate(targetUrl);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('fr-FR').format(amount) + ' FCFA';
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'completed':
        return 'âœ…';
      case 'pending':
        return 'â³';
      case 'warning':
        return 'âš ï¸';
      default:
        return 'â„¹ï¸';
    }
  };

  const getServiceIcon = (iconName) => {
    const icons = {
      'document': 'ğŸ“„',
      'users': 'ğŸ‘¥',
      'chart': 'ğŸ“Š',
      'user': 'ğŸ‘¤',
      'banknotes': 'ğŸ’°',
      'document-check': 'ğŸ“‹',
      'pencil': 'âœï¸',
      'academic-cap': 'ğŸ“',
      'cog': 'âš™ï¸',
      'bell': 'ğŸ””',
      'file-alt': 'ğŸ“‘',
      'certificate': 'ğŸ“œ',
      'calendar': 'ğŸ“…',
      'phone': 'ğŸ“',
      'envelope': 'âœ‰ï¸',
      'shield': 'ğŸ›¡ï¸',
      'heart': 'â¤ï¸',
      'star': 'â­',
      'home': 'ğŸ ',
      'info': 'â„¹ï¸',
      'question': 'â“',
      'check': 'âœ”ï¸',
      'times': 'âŒ',
      'exclamation': 'â—',
      'plus': 'â•',
      'minus': 'â–',
      'arrow-right': 'â¡ï¸',
      'arrow-left': 'â¬…ï¸',
      'arrow-up': 'â¬†ï¸',
      'arrow-down': 'â¬‡ï¸',
      'search': 'ğŸ”',
      'reclamation': 'ğŸ“¢',
    };
    return icons[iconName] || 'ğŸ“Œ';
  };

  // Fonction amÃ©liorÃ©e pour afficher le titre "M." ou "Mme" en fonction du sexe
  const getGenderTitle = (sexe) => {
    if (!sexe) return ''; // Si pas de sexe dÃ©fini, ne pas afficher de titre
    
    // GÃ©rer diffÃ©rents formats possibles
    const sexeNormalized = sexe.toString().toUpperCase();
    
    switch (sexeNormalized) {
      case 'M':
      case 'MASCULIN':
      case 'HOMME':
        return 'M.';
      case 'F':
      case 'FEMININ':
      case 'FEMME':
        return 'Mme';
      default:
        return ''; // Si format non reconnu, ne pas afficher de titre
    }
  };

  // Fonction pour gÃ©nÃ©rer un message de bienvenue personnalisÃ©
  const getWelcomeMessage = (user, userType) => {
    const title = getGenderTitle(user.sexe);
    const fullName = `${user.prenoms} ${user.nom}`;
    
    if (userType === 'actif') {
      return `Bienvenue ${title} ${fullName} !`;
    } else {
      return `Bienvenue ${title} ${fullName} !`;
    }
  };

  if (loading) {
    return (
      <div className="dashboard">
        <Header />
        <div className="dashboard__loading">
          <div className="dashboard__spinner"></div>
          <p>Chargement de votre tableau de bord...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dashboard">
        <Header />
        <div className="dashboard__error">
          <h2>Erreur</h2>
          <p>{error}</p>
          <button onClick={() => fetchDashboardData()}>RÃ©essayer</button>
        </div>
      </div>
    );
  }

  const { user, dashboard, user_type } = dashboardData;

  return (
    <div className="dashboard">
      <Header />
      
      <main className="dashboard__main">
        <div className="dashboard__container">
          
          {/* Welcome Section */}
          <section className="dashboard__welcome">
            <div className="dashboard__welcome-content">
              <div className="dashboard__welcome-text">
                <h1 className="dashboard__title">
                  {getWelcomeMessage(user, user_type)}
                </h1>
              </div>

              <div className="dashboard__welcome-actions">
                <button 
                  className="dashboard__btn"
                  onClick={() => handleServiceClick('profil')}
                  type="button"
                >
                  Editer mon profil
                </button>
              </div>

              <div className="dashboard__welcome-actions">
                <button 
                  className="dashboard__logout-btn"
                  onClick={handleLogout}
                  type="button"
                >
                  DÃ©connexion
                </button>
              </div>
            </div>
          </section>

          

          {/* Stats Cards */}
          <section className="dashboard__stats">
            <div className="dashboard__stats-grid">
              {user_type === 'actif' ? (
                <>
                  <div className="dashboard__stat-card dashboard__stat-card--primary">
                    <div className="dashboard__stat-icon">ğŸ’°</div>
                    <div className="dashboard__stat-content">
                      <h3 className="dashboard__stat-title">Cotisations Totales</h3>
                      <p className="dashboard__stat-value">
                        {formatCurrency(dashboard.stats.cotisations_totales)}
                      </p>
                    </div>
                  </div>
                  
                  <div className="dashboard__stat-card dashboard__stat-card--success">
                    <div className="dashboard__stat-icon">ğŸ</div>
                    <div className="dashboard__stat-content">
                      <h3 className="dashboard__stat-title">Prestations ReÃ§ues</h3>
                      <p className="dashboard__stat-value">
                        {formatCurrency(dashboard.stats.prestations_recues)}
                      </p>
                    </div>
                  </div>
                  
                  <div className="dashboard__stat-card dashboard__stat-card--info">
                    <div className="dashboard__stat-icon">ğŸ“„</div>
                    <div className="dashboard__stat-content">
                      <h3 className="dashboard__stat-title">Attestations</h3>
                      <p className="dashboard__stat-value">
                        {dashboard.stats.attestations_demandees}
                      </p>
                    </div>
                  </div>
                  
                  <div className="dashboard__stat-card dashboard__stat-card--warning">
                    <div className="dashboard__stat-icon">ğŸ“‹</div>
                    <div className="dashboard__stat-content">
                      <h3 className="dashboard__stat-title">Dossiers en Cours</h3>
                      <p className="dashboard__stat-value">
                        {dashboard.stats.dossiers_en_cours}
                      </p>
                    </div>
                  </div>
                </>
              ) : (
                <>
                  <div className="dashboard__stat-card dashboard__stat-card--primary">
                    <div className="dashboard__stat-icon">ğŸ’°</div>
                    <div className="dashboard__stat-content">
                      <h3 className="dashboard__stat-title">Pension Mensuelle</h3>
                      <p className="dashboard__stat-value">
                        {formatCurrency(dashboard.stats.pension_mensuelle)}
                      </p>
                    </div>
                  </div>
                  
                  <div className="dashboard__stat-card dashboard__stat-card--success">
                    <div className="dashboard__stat-icon">ğŸ“…</div>
                    <div className="dashboard__stat-content">
                      <h3 className="dashboard__stat-title">Pensions ReÃ§ues</h3>
                      <p className="dashboard__stat-value">
                        {dashboard.stats.pensions_recues} mois
                      </p>
                    </div>
                  </div>
                  
                  <div className="dashboard__stat-card dashboard__stat-card--info">
                    <div className="dashboard__stat-icon">ğŸ’µ</div>
                    <div className="dashboard__stat-content">
                      <h3 className="dashboard__stat-title">Total PerÃ§u</h3>
                      <p className="dashboard__stat-value">
                        {formatCurrency(dashboard.stats.total_percu)}
                      </p>
                    </div>
                  </div>
                  
                  <div className="dashboard__stat-card dashboard__stat-card--warning">
                    <div className="dashboard__stat-icon">ğŸ“‹</div>
                    <div className="dashboard__stat-content">
                      <h3 className="dashboard__stat-title">Certificats Valides</h3>
                      <p className="dashboard__stat-value">
                        {dashboard.stats.certificats_valides}
                      </p>
                    </div>
                  </div>
                </>
              )}
            </div>
          </section>

          <div className="dashboard__content-grid">
            
            {/* Services Section */}
            <section className="dashboard__services">
              <h2 className="dashboard__section-title">Services Disponibles</h2>
              <div className="dashboard__services-grid">
                {dashboard.services_disponibles.map(service => (
                  <div 
                    key={service.id}
                    className={`dashboard__service-card ${!service.available ? 'dashboard__service-card--disabled' : ''}`}
                    onClick={() => service.available && handleServiceClick(service.id)}
                    style={{ cursor: service.available ? 'pointer' : 'not-allowed' }}
                  >
                    <div className="dashboard__service-icon">
                      {getServiceIcon(service.icon)}
                    </div>
                    <div className="dashboard__service-content">
                      <h3 className="dashboard__service-title">{service.name}</h3>
                      <p className="dashboard__service-description">{service.description}</p>
                    </div>
                    <div className="dashboard__service-arrow">â†’</div>
                  </div>
                ))}
              </div>
            </section>

            {/* Activities Section */}
            <section className="dashboard__activities">
              <h2 className="dashboard__section-title">ActivitÃ©s RÃ©centes</h2>
              <div className="dashboard__activities-list">
                {dashboard.activites_recentes.map(activite => (
                  <div key={activite.id} className="dashboard__activity-item">
                    <div className="dashboard__activity-icon">
                      {getStatusIcon(activite.status)}
                    </div>
                    <div className="dashboard__activity-content">
                      <p className="dashboard__activity-description">
                        {activite.description}
                      </p>
                      <p className="dashboard__activity-date">
                        {new Date(activite.date).toLocaleDateString('fr-FR', {
                          day: 'numeric',
                          month: 'long',
                          year: 'numeric'
                        })}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            </section>

          </div>

        </div>
      </main>
    </div>
  );
};

export default Dashboard;
