import React, { useState, useEffect } from 'react';

const Header = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 50);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`
      fixed top-0 w-full z-50 transition-all duration-500 ease-in-out
      ${scrolled ? 'bg-cppf-dark-blue/95 backdrop-blur-lg shadow-2xl' : 'bg-gradient-to-r from-cppf-dark-blue to-cppf-blue'}
    `}>
      <div className="container mx-auto px-6 py-4">
        <div className="flex justify-between items-center">
          {/* Logo avec effet hover 3D */}
          <div className="flex items-center space-x-4 group cursor-pointer">
            <div className="relative">
              <img 
                src="/images/cppf.png" 
                alt="CPPF" 
                className="h-14 w-14 transition-transform duration-300 group-hover:scale-110 group-hover:rotate-3"
              />
              <div className="absolute inset-0 bg-cppf-gradient rounded-full opacity-0 group-hover:opacity-20 transition-opacity duration-300"></div>
            </div>
            <div className="transform group-hover:translate-x-2 transition-transform duration-300">
              <h1 className="text-2xl font-bold bg-gradient-to-r from-white to-cppf-yellow bg-clip-text text-transparent">
                CPPF
              </h1>
              <p className="text-sm text-cppf-yellow font-medium">E-Services</p>
            </div>
          </div>

          {/* Navigation avec effets premium */}
          <nav className="hidden md:flex space-x-8">
            {['Accueil', 'Services', 'Contact'].map((item) => (
              <a
                key={item}
                href="#"
                className="
                  relative text-white font-medium text-lg
                  before:absolute before:bottom-0 before:left-0 before:w-0 before:h-0.5 
                  before:bg-cppf-gradient before:transition-all before:duration-300
                  hover:before:w-full hover:text-cppf-yellow
                  transform hover:-translate-y-1 transition-all duration-300
                "
              >
                {item}
              </a>
            ))}
          </nav>

          {/* Bouton CTA avec effet glassmorphism */}
          <button className="
            hidden lg:block px-6 py-3 rounded-full font-semibold
            bg-white/10 backdrop-blur-lg border border-white/20 text-white
            hover:bg-cppf-gradient hover:scale-105 hover:shadow-2xl
            transition-all duration-300 transform
          ">
            Connexion
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
