import React, { useState, useEffect } from 'react';
import { userService, agentService, retraiteService } from '../../../services/api';
import './EditProfile.css';

const EditProfile = ({ userType }) => {
  const [formData, setFormData] = useState({
    telephone: '',
    email: '',
    password: '',
    passwordConfirmation: '',
    numPension: '',
    dateFinService: '',
  });
  
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Récupérer les informations actuelles de l'utilisateur
    const getUserData = async () => {
      setLoading(true);
      try {
        const response = userType === 'actif' ? await agentService.getProfile() : await retraiteService.getProfile();
        setFormData({
          ...formData,
          telephone: response.data.telephone,
          email: response.data.email,
          numPension: response.data.numero_pension || '',
          dateFinService: response.data.date_prise_service || '',
        });
      } catch (error) {
        setMessage('Erreur lors du chargement des données');
      } finally {
        setLoading(false);
      }
    };
    
    getUserData();
  }, [userType]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.telephone) newErrors.telephone = 'Le numéro de téléphone est requis';
    if (!formData.email) newErrors.email = 'L\'email est requis';
    if (formData.password && formData.password !== formData.passwordConfirmation) newErrors.password = 'Les mots de passe ne correspondent pas';
    if (formData.password && formData.password.length < 8) newErrors.password = 'Le mot de passe doit contenir au moins 8 caractères';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);
    setMessage('');
    try {
      const updateData = { ...formData };
      const response = userType === 'actif' ? await agentService.updateProfile(updateData) : await retraiteService.updateProfile(updateData);
      
      if (response.data.success) {
        setMessage('Profil mis à jour avec succès !');
      } else {
        setMessage('Erreur lors de la mise à jour du profil');
      }
    } catch (error) {
      setMessage('Erreur serveur');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="edit-profile">
      <h2 className="edit-profile__title">Modifier votre profil</h2>

      {message && <div className="edit-profile__message">{message}</div>}

      <form className="edit-profile__form" onSubmit={handleSubmit}>
        <div className="edit-profile__group">
          <label htmlFor="telephone" className="edit-profile__label">Numéro de téléphone</label>
          <input
            type="text"
            id="telephone"
            name="telephone"
            value={formData.telephone}
            onChange={handleChange}
            className={`edit-profile__input ${errors.telephone ? 'edit-profile__input--error' : ''}`}
            placeholder="Téléphone"
          />
          {errors.telephone && <div className="edit-profile__error">{errors.telephone}</div>}
        </div>

        <div className="edit-profile__group">
          <label htmlFor="email" className="edit-profile__label">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className={`edit-profile__input ${errors.email ? 'edit-profile__input--error' : ''}`}
            placeholder="Email"
          />
          {errors.email && <div className="edit-profile__error">{errors.email}</div>}
        </div>

        {userType === 'actif' && (
          <>
            <div className="edit-profile__group">
              <label htmlFor="dateFinService" className="edit-profile__label">Date de fin de service</label>
              <input
                type="date"
                id="dateFinService"
                name="dateFinService"
                value={formData.dateFinService}
                onChange={handleChange}
                className="edit-profile__input"
              />
            </div>
          </>
        )}

        {userType === 'retraite' && (
          <div className="edit-profile__group">
            <label htmlFor="numPension" className="edit-profile__label">Numéro de pension</label>
            <input
              type="text"
              id="numPension"
              name="numPension"
              value={formData.numPension}
              onChange={handleChange}
              className="edit-profile__input"
              placeholder="Numéro de pension"
            />
          </div>
        )}

        <div className="edit-profile__group">
          <label htmlFor="password" className="edit-profile__label">Nouveau mot de passe</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className="edit-profile__input"
            placeholder="Mot de passe"
          />
        </div>

        <div className="edit-profile__group">
          <label htmlFor="passwordConfirmation" className="edit-profile__label">Confirmer le mot de passe</label>
          <input
            type="password"
            id="passwordConfirmation"
            name="passwordConfirmation"
            value={formData.passwordConfirmation}
            onChange={handleChange}
            className="edit-profile__input"
            placeholder="Confirmer le mot de passe"
          />
        </div>

        <button type="submit" className="edit-profile__button" disabled={loading}>
          {loading ? 'Mise à jour...' : 'Mettre à jour'}
        </button>
      </form>
    </div>
  );
};

export default EditProfile;
