import React from 'react';
import Header from '../components/Header';

const Home = () => {
  return (
    <>
      <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
        <Header />

        {/* Hero Section avec parallax et animations */}
        <section className="relative min-h-screen flex items-center justify-center overflow-hidden pt-20">
          {/* Background animé */}
          <div className="absolute inset-0 bg-cppf-gradient opacity-10"></div>
          <div
            className="absolute inset-0 animate-pulse"
            style={{
              backgroundImage:
                "url(\"data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%239C92AC' fill-opacity='0.1'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E\")",
            }}
          ></div>

          <div className="relative z-10 text-center max-w-6xl mx-auto px-6">
            {/* Titre principal avec effet typewriter */}
            <h1 className="text-4xl md:text-6xl font-bold mb-8">
              <span className="bg-cppf-gradient bg-clip-text text-transparent">
                Bienvenue sur
              </span>
              <br />
              <span className="text-cppf-dark-blue">CPPF E-Services</span>
            </h1>

            {/* Sous-titre avec animation */}
            <p className="text-xl md:text-2xl text-gray-600 mb-12 animate-fade-in-up">
              Caisse des Pensions et des Prestations Familiales des agents de l'État
            </p>

            {/* Bouton CTA époustouflant */}
            <button
              className="
                group relative px-12 py-6 text-xl font-bold text-white rounded-full
                bg-cppf-gradient shadow-2xl transform hover:scale-110
                transition-all duration-500 hover:shadow-cppf-blue/50
                overflow-hidden
              "
            >
              <span className="relative z-10">Découvrir nos Services</span>
              <div
                className="
                  absolute inset-0 bg/20 translate-x-full group-hover:translate-x-0
                  transition-transform duration-500 ease-in-out
                "
              ></div>
            </button>
          </div>

          {/* Éléments décoratifs flottants */}
          <div className="absolute top-20 left-10 w-20 h-20 bg-cppf-green/20 rounded-full animate-bounce"></div>
          <div className="absolute bottom-20 right-10 w-16 h-16 bg-cppf-yellow/20 rounded-full animate-bounce delay-1000"></div>
          <div className="absolute top-1/2 left-20 w-12 h-12 bg-cppf-blue/20 rounded-full animate-pulse"></div>
        </section>
      </div>
    </>
  );
};

export default Home;
