// File: frontend/src/pages/simulateur/SimulateurPension.jsx

import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../../components/Header';
import { utils } from '../../services/api';
import './SimulateurPension.css';

const SimulateurPension = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('profile');
  const [userProfile, setUserProfile] = useState(null);
  const [simulationData, setSimulationData] = useState(null);
  const [simulationHistory, setSimulationHistory] = useState([]);
  const [customSimulation, setCustomSimulation] = useState({
    indice: '',
    dateRetraite: ''
  });

  const makeApiCall = useCallback(async (endpoint, options = {}) => {
    console.log('ðŸ”„ Appel API:', endpoint);
    const baseUrl = 'http://localhost:8000/api';
    const url = `${baseUrl}${endpoint}`;

    const defaultOptions = {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        'Content-Type': 'application/json',
        'Accept': 'application/json',
      },
    };

    try {
      console.log('ðŸ“¡ URL complÃ¨te:', url);
      const response = await fetch(url, {
        ...defaultOptions,
        ...options,
        headers: {
          ...defaultOptions.headers,
          ...options.headers
        }
      });

      console.log('ðŸ“Š Statut rÃ©ponse:', response.status);

      if (!response.ok) {
        const errorText = await response.text();
        console.error('âŒ Erreur rÃ©ponse:', errorText);
        throw new Error(`API error: ${response.status} - ${response.statusText}`);
      }

      const data = await response.json();
      console.log('âœ… DonnÃ©es reÃ§ues:', data);
      return data;
    } catch (error) {
      console.error('âŒ Erreur de l\'API:', error);
      throw error;
    }
  }, []);

  const loadProfile = useCallback(async () => {
    try {
      console.log('ðŸ”„ Chargement du profil...');
      const data = await makeApiCall('/actifs/simulateur-pension/profil');
      
      if (data.success) {
        console.log('âœ… Profil chargÃ©:', data.profile);
        setUserProfile(data.profile);
        return data.profile;
      } else {
        throw new Error(data.message || 'Erreur lors du chargement du profil');
      }
    } catch (error) {
      console.error('âŒ Erreur profil:', error);
