                          <div className="step-formula">
                            SB Ã— Taux = {formatCurrency(simulationData.salaireReference)} Ã— {simulationData.tauxLiquidation}%
                          </div>
                        </div>

                        {simulationData.coefficientTemporel && (
                          <div className="step">
